package main

import (
	"net/http"
	"encoding/json"
	"strings"
	"flag"
	"log"
	"fmt"

	"github.com/pborman/uuid"
)

var (
	port int
)

const (
	screenshot = ""
)

func handler() http.Handler {
	mux := http.NewServeMux()
	mux.HandleFunc("/wd/hub/session", func(w http.ResponseWriter, r *http.Request) {
		if r.Method != http.MethodPost {
			http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
			return
		}
		sessionId := uuid.New()
		json.NewEncoder(w).Encode(struct {
			S string `json:"sessionId"`
		}{sessionId})
	})
	mux.HandleFunc("/wd/hub/session/", func(w http.ResponseWriter, r *http.Request) {
		fragments := strings.Split(r.URL.Path, "/")
		sessionId := fragments[4]
		json.NewEncoder(w).Encode(struct {
			St string `json:"state"`
			Id string `json:"sessionId"`
			Value string `json:"value"`
		}{"success", sessionId, screenshot})
		if r.Method != http.MethodDelete || len(fragments) != 4 {
			return
		}
	})
	return mux
}

func init() {
	flag.IntVar(&port, "port", 4444, "port to bind to")
	flag.Parse()
}

func main() {
	listen := fmt.Sprintf(":%d", port)
	log.Println("listening on", listen)
	log.Print(http.ListenAndServe(listen, handler()))
}